import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';
import agendamentos from '../data';

const AgendamentoScreen = () => {
  const [nome, setNome] = useState('');
  const [cpf, setCpf] = useState('');
  const [dataNascimento, setDataNascimento] = useState('');
  const [endereco, setEndereco] = useState('');

  const agendarConsulta = () => {
    const paciente = {
      nome,
      cpf,
      dataNascimento,
      endereco,
    };

    agendamentos.push({
      id: agendamentos.length + 1,
      paciente,
      dataConsulta: 'Data da consulta', 
      valorCobrado: 0, 
    });


    setNome('');
    setCpf('');
    setDataNascimento('');
    setEndereco('');

    console.log('Dados do paciente:', paciente);
    console.log('Agendamentos atualizados:', agendamentos);
  };

  return (
    <View style={styles.container}>
      <Text style={{padding:20}}>Coloque suas informações para o agendamento:</Text>
      <View style={styles.inputs}>
      <Text>Nome:</Text>
      <TextInput
        style={{borderWidth: 2, marginBottom: 3, borderColor: 'black'}}
        onChangeText={(text) => setNome(text)}
        value={nome}
      />
      <Text>CPF:</Text>
      <TextInput
        onChangeText={(text) => setCpf(text)}
        value={cpf}
      />
      <Text>Data de Nascimento:</Text>
      <TextInput
        onChangeText={(text) => setDataNascimento(text)}
        value={dataNascimento}
      />
      <Text>Endereço:</Text>
      <TextInput
        onChangeText={(text) => setEndereco(text)}
        value={endereco}
      />
      </View>
      <Button title="Agendar Consulta" onPress={agendarConsulta} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    alignContent: 'center',
    alignSelf: 'center',
  },
  inputs: {
    padding: 30,
  }
})

export default AgendamentoScreen;
