import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, ScrollView } from 'react-native';

const FruitShoppingList = () => {
  const [fruits, setFruits] = useState([]);
  const [newFruit, setNewFruit] = useState('');

  const addFruit = () => {
    if (newFruit) {
      setFruits([...fruits, newFruit]);
      setNewFruit('');
    }
  };

  const removeFruit = (index) => {
    const newFruits = [...fruits];
    newFruits.splice(index, 1);
    setFruits(newFruits);
  };

  return (
    <View style={{ flex: 1, padding: 20 }}>
      <TextInput
        placeholder="Digite uma fruta"
        value={newFruit}
        onChangeText={(text) => setNewFruit(text)}
      />
      <TouchableOpacity onPress={addFruit}>
        <Text>Adicionar</Text>
      </TouchableOpacity>
      <ScrollView style={{ marginTop: 20 }}>
        {fruits.map((fruit, index) => (
          <View
            key={index}
            style={{
              flexDirection: 'row',
              justifyContent: 'space-between',
              alignItems: 'center',
              padding: 10,
              borderBottomWidth: 1,
              borderColor: 'gray',
            }}
          >
            <Text>{fruit}</Text>
            <TouchableOpacity onPress={() => removeFruit(index)}>
              <Text style={{ color: 'red' }}>Remover</Text>
            </TouchableOpacity>
          </View>
        ))}
      </ScrollView>
    </View>
  );
};

export default FruitShoppingList;
